async markup({ content, filename }) {
      let code = content;
      let map;
      let attributes;
      let toString2;
      const dependencies = [];
      for (const pp of preprocessors) {
        const processed = await preprocess(code, pp, { filename });
        if (processed && processed.dependencies) {
          dependencies.push(...processed.dependencies);
        }
        code = processed ? processed.code : code;
        map = processed.map ?? map;
        attributes = processed.attributes ?? attributes;
        toString2 = processed.toString ?? toString2;
      }
      return {
        code,
        dependencies,
        map,
        attributes,
        toString: toString2
      };
    }

preprocess.script:
({ content, filename }) => {
		if (!filename) return;

		const basename = path.basename(filename);
		if (basename.startsWith('+page.') || basename.startsWith('+layout.')) {
			const match = content.match(options_regex);
			if (match) {
				const fixed = basename.replace('.svelte', '(.server).js/ts');

				const message =
					`\n${colors.bold().red(path.relative('.', filename))}\n` +
					`\`${match[1]}\` will be ignored — move it to ${fixed} instead. See https://kit.svelte.dev/docs/page-options for more information.`;

				if (!warned.has(message)) {
					console.log(message);
					warned.add(message);
				}
			}
		}
	}

preprocess.markup:
({ content, filename }) => {
		if (!filename) return;

		const basename = path.basename(filename);
		const has_children =
			content.includes('<slot') || (isSvelte5Plus() && content.includes('{@render'));

		if (basename.startsWith('+layout.') && !has_children) {
			const message =
				`\n${colors.bold().red(path.relative('.', filename))}\n` +
				`\`<slot />\`${isSvelte5Plus() ? ' or `{@render ...}` tag' : ''}` +
				' missing — inner content will not be rendered';

			if (!warned.has(message)) {
				console.log(message);
				warned.add(message);
			}
		}
	}
